(29, 1, '2022-12-23 23:32:57', '2022-12-23 23:32:57', '<!-- wp:media-text {\"mediaId\":13,\"mediaLink\":\"http://localhost/wordpress/photo_2022-12-23_14-14-11-2/\",\"mediaType\":\"image\",\"mediaWidth\":47} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:47% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/wordpress/wp-content/uploads/2022/12/photo_2022-12-23_14-14-11-2.jpg\" alt=\"\" class=\"wp-image-13 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Bishopgate Gardens are new apartments for sale in Preston city center, UK. Bishopgate Gardens offers modern &amp; superior residences in the heart of Preston city.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Bishopgate Gardens has a choice of one, two and three-bedroom homes with exceptional communal spaces &amp; retail units on the ground floor for utmost convenience.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>one bed from ETB 700</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>two bed from ETB 1200</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Deluxes from ETB 2500</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>30% discount on July 14</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:gallery {\"imageCrop\":false,\"linkTo\":\"none\",\"backgroundColor\":\"white\"} -->\n<figure class=\"wp-block-gallery has-nested-images columns-default has-white-background-color has-background\"><!-- wp:image {\"id\":87,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-full is-style-default\"><img src=\"http://localhost/wordpress/wp-content/uploads/2022/12/ing2-2.jpg\" alt=\"\" class=\"wp-image-87\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":88,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost/wordpress/wp-content/uploads/2022/12/img2-2.jpg\" alt=\"\" class=\"wp-image-88\"/></figure>\n<!-- /wp:image --></figure>\n<!-- /wp:gallery -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->', 'Bishopgate Garden', '', 'publish', 'closed', 'closed', '', 'bishopgate-garden', '', '', '2022-12-25 13:38:44', '2022-12-25 13:38:44', '', 0, 'http://localhost/wordpress/?post_type=properties&#038;p=29', 0, 'properties', '', 0),
